"use client";

import Image from "next/image";

interface NotionImageProps {
  src: string;
  alt: string;
  style?: React.CSSProperties;
}

export function NotionImage({ src, alt, style }: NotionImageProps) {
  return (
    <Image
      src={src}
      alt={alt}
      width={800}
      height={600}
      style={style}
      className="notion-image"
      priority={false}
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
    />
  );
}
